    }

    return(
        <div className = "todo">
            <li className="todo-item">{text}</li>
            <button className="complete-btn" >
                <i className="fas fa-chek">âœ”</i>
            </button>
            <button className="trash-btn" onClick={deleteHandler}>
                <i className="fas fa-trash">Del</i>
            </button>
        </div>

    );
};

